mapper.insert:
insert into sys_menu (id, name, parent_id, 
  url,nested_url,perms, type, hidden, icon,
  order_num, create_by, create_time, 
  last_update_by, last_update_time, del_flag
  )
values (#{id,jdbcType=BIGINT}, #{name,jdbcType=VARCHAR}, #{parentId,jdbcType=BIGINT}, 
  #{url,jdbcType=VARCHAR}, #{nested_url,jdbcType=VARCHAR}, #{perms,jdbcType=VARCHAR}, #{type,jdbcType=INTEGER},#{hidden,jdbcType=TINYINT} #{icon,jdbcType=VARCHAR},
  #{orderNum,jdbcType=INTEGER}, #{createBy,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{lastUpdateBy,jdbcType=VARCHAR}, #{lastUpdateTime,jdbcType=TIMESTAMP}, #{delFlag,jdbcType=TINYINT}
  )

mapper.select:

select

from sys_menu
where name like #{pattern}

select m.* from sys_menu m, sys_role_menu rm
where rm.role_id = #{roleId,jdbcType=BIGINT}
and m.id = rm.menu_id

select m.* from sys_menu m,sys_org_menu rm
where rm.org_id = #{orgId,jdbcType=BIGINT}
and  m.id = rm.menu_id
